export const planetChartData = {
    type: "line",
    chartData: {
        labels: ["Mon", "Tue", "Wed", "Thu", "Fri", "Sat", "Sun"],
        datasets: [
            {
                label: "Expected",
                data: [90, 120, 161, 134, 105, 160, 165],
                backgroundColor: "rgba(54,73,93,.5)",
                borderColor: "#36495d",
                borderWidth: 2,

            },
            {
                label: "Actual",
                data: [118, 82, 91, 154, 162, 140, 145],
                backgroundColor: "rgba(71, 183,132,.5)",
                borderColor: "#47b784",
                borderWidth: 2,

            }
        ]
    },
    options: {
        responsive: true,
        lineTension: 1,
        maintainAspectRatio: true ,
        animation: {
      duration: 2000,
      onProgress: function(context) {
        if (context.initial) {
          initProgress.value = context.currentStep / context.numSteps;
        } else {
          progress.value = context.currentStep / context.numSteps;
        }
      },
      onComplete: function(context) {
        if (context.initial) {
          console.log('Initial animation finished');
        } else {
          console.log('animation finished');
        }
      }
    },
        scales: {
            x: {
                title: {
                    display: true,
                    text: 'Day'
                },
                ticks: {
                    beginAtZero: true,
                    padding: 0,
                },
                Width : 600
            },
            y: {
                title: {
                    display: true,
                    text: 'Values'
                },
                min: 0,
                max: 200,
                ticks: {
                    stepSize: 10,
                    padding: 0,
                },

            }
        }
    }
};

export default planetChartData;
